    /** This method executes when the user clicks the select file button.
     * It lets the user select a file and then proceeds to execute the
     * instructions to send the file via TCP.
     **/
    private void chooseFileButtonMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_chooseFileButtonMouseClicked
        JFileChooser jfc = new JFileChooser(); // File browser to choose file
        jfc.setFileSelectionMode(JFileChooser.FILES_ONLY); // Only files can be chosen (not directories)
        if (jfc.isMultiSelectionEnabled()) { // Only one file at a time (no multiple selection)
            jfc.setMultiSelectionEnabled(false);
        }

        int r = jfc.showOpenDialog(null);
        
        System.out.println("Ready to send file once file has been selected.");
        if (r == JFileChooser.APPROVE_OPTION) {
            try {
                // Store file and the file name in variables 
                file = jfc.getSelectedFile();
                fileName = file.getName();
                // Transfer the file name
                dos.writeUTF(fileName);
                // Create a buffered file input stream for reading the file
                // into a byte array
                FileInputStream fis = new FileInputStream(file);
                BufferedInputStream bis = new BufferedInputStream(fis);
                // Initialize the byte array that will get the file's data
                // read into
                byte[] contents;
                // Store the file size into a variable and transfer the
                // file size value to the receiver
                long fileSize = file.length();
                dos.writeLong(fileSize);
                
                // This is the algorithm that sends the file to the receiver
                // via TCP. It does so in seperate chunks of the file. It also
                // computes the progress of transfer and prints it to the 
                // terminal.
                long current = 0;
                while(current!=fileSize){
                    int size = 10000;
                    if(fileSize - current >= size) {         
                        current += size;                 
                    } else {                  
                        size = (int)(fileSize - current);
                        current = fileSize;
                    }                  
                    contents = new byte[size];    
                    bis.read(contents, 0, size);           
                    os.write(contents);
                    int progress = (int)((current*100)/fileSize);
                    System.out.println("Sending file ... "+progress+"% complete!");
                }
                os.flush();    
                System.out.println("File sent succesfully!");
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
        
    }//GEN-LAST:event_chooseFileButtonMouseClicked

