        // Create a new Server Socket for the receiver to connect to.
        // It listens for a new socket connection and accepts it when a
        // receiver wants to connect.
        // Once the receiver has connected, confirmation is printed to the
        // terminal and a data output stream is created for the new socket
        // connection.
        try {
            ServerSocket ssock = new ServerSocket(5000);
            socket = ssock.accept();
            System.out.println("Receiver connected");
            os = socket.getOutputStream();
            dos = new DataOutputStream(os);
        } catch (IOException e) {
            e.printStackTrace();
        }
        
